Scroller={speed:10,offset:0,gy:function(e){var t=e.offsetTop;if(e.offsetParent){while(e=e.offsetParent){t+=e.offsetTop}}return t-this.offset},scrollTop:function(){var e=document.body;var t=document.documentElement;if(e&&e.scrollTop)return e.scrollTop;if(t&&t.scrollTop)return t.scrollTop;if(window.pageYOffset)return window.pageYOffset;return 0},add:function(e,t,n){if(e.addEventListener)return e.addEventListener(t,n,false);if(e.attachEvent)return e.attachEvent("on"+t,n)},end:function(e){if(window.event){window.event.cancelBubble=true;window.event.returnValue=false;return}if(e.preventDefault&&e.stopPropagation){e.preventDefault();e.stopPropagation()}},scroll:function(e){var t=window.innerHeight||document.documentElement.clientHeight;var n=document.body.scrollHeight;var r=Scroller.scrollTop();if(n-e<t){e=n-t}var i=(e-r)/Scroller.speed;if(e>r){i=Math.ceil(i)}else{i=Math.floor(i)}r+=i;window.scrollTo(0,r);Scroller.previousPos=r;if(r==e||i==0){clearInterval(Scroller.interval);Scroller.previousPos=false}},init:function(){Scroller.add(window,"load",Scroller.render)},render:function(){Scroller.end(this);var e=document.getElementsByTagName("a");for(var t=0;t<e.length;t++){var n=e[t];if(n.href&&n.href.indexOf("#")!=-1&&n.href.indexOf("#")!=n.href.length-1&&(n.pathname==location.pathname||"/"+n.pathname==location.pathname)){Scroller.add(n,"click",function(){Scroller.end(this);var t=this.hash.substr(1);var n=document.getElementById(t);if(!n){for(var r=0;r<e.length;r++){if(e[r].name==t){n=e[r];break}}}if(n){clearInterval(Scroller.interval);Scroller.interval=setInterval(function(){Scroller.scroll(Scroller.gy(n))},10)}})}}}};Scroller.init()
