Scroller={speed:10,gy:function(elem){var y=elem.offsetTop;if(elem.offsetParent){while(elem=elem.offsetParent){y+=elem.offsetTop;}}
return y;},scrollTop:function(){var body=document.body;var d=document.documentElement;if(body&&body.scrollTop)return body.scrollTop;if(d&&d.scrollTop)return d.scrollTop;if(window.pageYOffset)return window.pageYOffset;return 0;},add:function(elem,type,func){if(elem.addEventListener)return elem.addEventListener(type,func,false);if(elem.attachEvent)return elem.attachEvent('on'+type,func);},end:function(e){if(window.event){window.event.cancelBubble=true;window.event.returnValue=false;return;}
if(e.preventDefault&&e.stopPropagation){e.preventDefault();e.stopPropagation();}},scroll:function(targetPos){var viewHeight=window.innerHeight||document.documentElement.clientHeight;var pageHeight=document.body.scrollHeight;var currentPos=Scroller.scrollTop();if(pageHeight-targetPos<viewHeight){targetPos=pageHeight-viewHeight;}
var diff=(targetPos-currentPos)/Scroller.speed;if(targetPos>currentPos){diff=Math.ceil(diff);}else{diff=Math.floor(diff);}
currentPos+=diff;window.scrollTo(0,currentPos);Scroller.previousPos=currentPos;if(currentPos==targetPos||diff==0){clearInterval(Scroller.interval);Scroller.previousPos=false;}},init:function(){Scroller.add(window,'load',Scroller.render);},render:function(){Scroller.end(this);var anchors=document.getElementsByTagName('a');for(var i=0;i<anchors.length;i++){var anchor=anchors[i];if(anchor.href&&anchor.href.indexOf('#')!=-1&&anchor.href.indexOf('#')!=anchor.href.length-1&&((anchor.pathname==location.pathname)||('/'+anchor.pathname==location.pathname))){Scroller.add(anchor,'click',function(){Scroller.end(this);var target=this.hash.substr(1);var targetElem=document.getElementById(target);if(!targetElem){for(var j=0;j<anchors.length;j++){if(anchors[j].name==target){targetElem=anchors[j];break;}}}
if(targetElem){clearInterval(Scroller.interval);Scroller.interval=setInterval(function(){Scroller.scroll(Scroller.gy(targetElem));},10);}});}}}};Scroller.init();